import boto3

SNS = boto3.client("sns")


class SNSNotificationPublisher:
    @classmethod
    def publish_message(cls, topic_arn: str, subject: str, message_body: str) -> None:
        SNS.publish(
            TopicArn=topic_arn,
            Subject=subject,
            Message=message_body,
        )


class ConsoleNotificationPublisher:
    @classmethod
    def publish_message(cls, topic_arn: str, subject: str, message_body: str) -> None:
        print(
            f"{topic_arn}: Message | Subject = {subject} | Message body: {message_body}"
        )
